import axios from 'axios'
import React, {useState, useEffect} from 'react'
import { NavLink } from "react-router-dom"

const Criptomonedas = () => {

    const [busqueda, setBusqueda] = useState("")
    const [criptomonedas, setCriptomonedas] = useState([])

    //const URL = 'https://api.coingecko.com/api/v3/coins'
    const URL = 'http://localhost:3000/api/criptomonedas'
    
    const traerDatos = () => {
        axios.get(URL, {
            headers: {
                'Authorization':'Bearer '+(localStorage.getItem("token")).toString()
            }
        }).then( (res) => {
            console.log(res.data)
            setCriptomonedas(res.data)
        } )
    }

    useEffect( () => {
        traerDatos()
    }, [] )

    const buscador = (e) => {
        setBusqueda(e.target.value)
    }

    const resultados = !busqueda ? criptomonedas : criptomonedas.filter((val) => val.name.toLowerCase().includes(busqueda.toLowerCase()))
    console.log(resultados)

  return (
    <>
    <input value={busqueda} onChange={buscador} type='text' placeholder='Buscar criptomonedas...' className='form-control'/>
    <table className='table table-dark table-hover mt-3'>
        <thead>
            <tr>
                <th>Símbolo</th>
                <th>Nombre</th>
                <th>Año de lanzamiento</th>
                <th>¿Es Stablecoin?</th>
            </tr>
        </thead>
        <tbody>
            { resultados.map( (resultado) => (
                <tr key={resultado.simbolo}>
                    <td>{resultado.simbolo}</td>
                    <td>
                        {/* <small><img src={resultado.image.small}/></small> */}
                        <NavLink 
                            to={{pathname: '/conversion'}}
                            state={{criptomoneda: resultado}}
                        > {resultado.nombre} 
                        </NavLink>
                    </td>
                    <td></td>
                    <td>{resultado.creacion}</td>
                    <td>
                        { resultado.esStablecoin == true ? (
                            <span className='badge bg-danger'> {resultado.esStablecoin} % </span>
                        ):(
                            <span className='badge bg-success'> {resultado.esStablecoin} % </span>
                        ) }
                    </td>
                </tr>
            ))}
        </tbody>
    </table>
    </>
  )
}

export default Criptomonedas
