import React, {useState, useEffect, Component} from 'react';
import { NavLink, useNavigate, Navigate } from "react-router-dom";
//import { getToken } from '../servicios/loginService'
import LoginService from "../servicios/loginService";


const Login = () => {

    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    //const [password, setPassword] = useState([])

    const state  = {
        username: username,
        password: password,
        loggedIn: false,
    };

    // console.log(state.criptomoneda)

    /*handleChange = (e) => {
        this.setState({[e.target.name]:e.target.value})
    }*/

    /*const handleChange = (e) => {
        setState({[e.target.name]:e.target.value})
    }*/

    const usrn = (e) => {
        setUsername(e.target.value);
    }

    const psswd = (e) => {
        setPassword(e.target.value);
    }

    useEffect( () => {
            const navigate = useNavigate();
            return () => {
                navigate("/criptomonedas");
            };
    }, [state.loggedIn]);


    const obtenerToken = async (e) => {
        e.preventDefault();

        /*await LoginService.getToken(this.state).then((res) => {
            console.log("RESPUESTA: " + res)
        }
        )*/

        try {
            await LoginService.getToken(state).then(
              () => {
                //const navigate = useNavigate();
                //navigate("/criptomonedas");
                //window.location.reload();
                state.loggedIn = true;
                console.log("LOGGED IN: " + state.loggedIn)

              },
              (error) => {
                console.log(error);
              }
            );
          } catch (err) {
            console.log(err);
          }

}


    
        return(
            <>
                <table className='table table-dark table-hover mt-3'>
                    <div className="container">
                        <div className="row">
                            <div className="panel panel-primary text-center">
                                
                                <br></br>
                                
                                <div className="panel-body" style={{ display: "flex", justifyContent: 'center' }}>
    
    
                                    <form onSubmit={obtenerToken}>
                                        <h3>Ingreso</h3>
    
                                        <br></br>
    
                                        <div className="mb-3">
                                        <label>Usuario</label>
                                        
                                        <input
                                            type="text"
                                            className="form-control"
                                            placeholder="Ingrese nombre de usuario"
                                            style={{width: "80vh", height:"4vh", textAlign:"center", justifyContent:"center", alignItems: "center"}}
                                            name="username"
                                            value={username}
                                            onChange={usrn}
                                        />
                                        
                                        </div>
    
                                        <div className="mb-3">
                                        <label>Contraseña</label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            placeholder="Ingrese contraseña"
                                            style={{width: "80vh", height:"4vh", textAlign:"center", justifyContent:"center", alignItems: "center"}}
                                            name="password"
                                            value={password}
                                            onChange={psswd}
                                        />
                                        </div>
    
                                        <div>
                                            
                                        </div>
                                        
                                        <div>
                                            <br></br><br></br><br></br><br></br><br></br><br></br>
                                            
                                            
                                            <button type="submit" className="btn btn-primary" style={{width: "30vh"}}>
                                                Iniciar Sesión
                                            </button>  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                            
                                            
                                            <button className="btn btn-success" style={{width: "30vh"}}>
                                                <NavLink to="/registro" style={{ textDecoration: 'none', color: 'inherit' }}>Registro</NavLink>
                                            </button>
                                        </div>

                                           {/*} {this.state.loggedIn && (
                                                <Navigate to="/criptomonedas" replace={true} />
                                           )} */}
                                        
                                    
                                    </form>

                                            
                                
                                </div>
    
                                <br></br>
    
                            </div>
                        </div>
                    </div>
                </table>
            </>
        )
    
    
}

export default Login;
